// TypeAheadDropDown.js
import '../index.css'
import React from 'react';


export default class TypeAheadDropDown extends React.Component {
 constructor(props) {
   super(props);
   this.state = {
     suggestions: [],
     text:''
   }
 }

 onTextChange = (e) => {
   let suggestions = [];
   const value = e.target.value;
   console.log(value);
   if (value.length > 0) {
      fetch("http://localhost:8080/typeahead?prefix=" + value + "&suggestionCount=15")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            suggestions: result,
            text:value
          });
        },

        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
   }

    this.setState(() => ({
      suggestions,
      text:value
    }));
 }

 suggestionSelected=(value)=>{
   this.setState(()=>({
     text:value,
     suggestions:[]
   }))
 }

 renderSuggestions = () => {
   const { suggestions } = this.state;
   if (suggestions.length === 0) {
     return null;
   }
   return (
     <ul>
       {suggestions.map(suggestion => <li key={suggestion} onClick={(e)=>this.suggestionSelected(suggestion)}>{suggestion}</li>)}
     </ul>
   )
 }


 render() {
   const {text}=this.state
   return (
   <div className="TypeAheadDropDown">
     <input onChange={this.onTextChange} placeholder="" value={text} type="text" />
     {this.renderSuggestions()}
   </div>
   );
 }

}
